package java_code_samples;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 5;
		int addresult = a+b; //addition operator = 15
		int subresult = a-b; //subtraction operator = 5
		int mulresult = a*b; //multiplication operator = 50
		int divresult = a/b; //division operator = 2
		int modresult = a%b; //modulus operator = 0
		int incresult = ++a; //increment operator = 11
		int decresult = --b; //decrement operator = 4
		System.out.println(addresult+"\n"+subresult+"\n"+mulresult);
		System.out.println(subresult);
		System.out.println(mulresult);
		System.out.println(divresult);
		System.out.println(modresult);
		System.out.println(incresult);
		System.out.println(decresult);

	}

}
