package java_code_samples;

public class AssignmentOperatorsEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		System.out.println(x+=5); //15
		System.out.println(x-=5); //10
		System.out.println(x*=5); //50
		System.out.println(x/=5); //10
		System.out.println(x%=5); //0
		System.out.println(x&=5); //0
		System.out.println(x|=5); //5
		System.out.println(x^=5); //0
		x = 14;//1110
		System.out.println(x);
		System.out.println(x>>=1); //right shift operator
		System.out.println(x<<=1); //left shift operator
		
		
		
		
		
		
		

	}

}
