package java_code_samples;

enum color{
	RED,
	BLUE,
	BLACK;
}

public class EnumExample {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		color c1 = color.RED;
		System.out.println(c1);
		color c2 = color.BLUE;
		System.out.println(c2);
		color c3 = color.BLACK;
		System.out.println(c3);

	}

}
