package java_code_samples;

public class VariableExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10; //integer variable
		char b = 'a'; //char variable
		String c = "Kashmeera"; //String variable
		Boolean d = true; //boolean variable
		Float f = (float)10.3; //float data type
		Double e = 25.67; //double data type
		System.out.println(a); //10
		System.out.println(b); //a
		System.out.println(c); //Kashmeera
		System.out.println(d); // true
		System.out.println(f); //10.3
		System.out.println(e); //25.67
		

	}

}
